import React from 'react'
import './App.css'
import Header from './../Header/Header';
// connecting to header file
class App extends React.Component{
    state={
        headerText : "Hello"
    }
    render(){
        return (
            <div>
                <Header/>
                <h2>{this.state.headerText}</h2>
            </div>
        )
    };
    };

